<?phpclass Articles{    private PDO $conn;    /**     * @param $conn     */    public function __construct($conn)    {        $this->conn = $conn;    }    public function getArticles(){        $query = '';        if($this->id) {            $query = " AND p.id ='".$this->id."'";        }        $sqlQuery = "                    SELECT p.id, p.title, p.message, p.category_id, u.first_name, u.last_name, p.status, p.created, p.updated, c.name as category                    FROM ".$this->postTable." AS p                    LEFT JOIN ".$this->categoryTable." AS c ON c.id = p.category_id                    LEFT JOIN ".$this->userTable." AS u ON u.id = p.userid                    WHERE p.status ='published' $query ORDER BY p.id DESC                    ";        $stmt = $this->conn->prepare($sqlQuery);        $stmt->execute();        return $stmt;    }}