<?phpclass Database{    private $host = "localhost";    private $user = "root";    private $password = "1";    private $database  = "phpzag_demo";    public function getConnection() {        try {            return new PDO($this->host, $this->user, $this->password, $this->database);        }        catch(PDOException $exception){            echo "Connection error: " . $exception->getMessage();            return null;        }    }}